import moment from 'moment';
moment.locale('zh-cn');
export default moment;
export function formatSecond(time) {
  return formatMilliseconds(time * 1000);
}
export function formatMilliseconds(time) {
  var datetime = new Date(time);
  var year = datetime.getFullYear();
  var month = datetime.getMonth() + 1;
  var day = datetime.getDate();
  var hour = datetime.getHours();
  var minute = datetime.getMinutes();
  var second = datetime.getSeconds();
  return "".concat(year, "-").concat(month.toString().padStart(2, '0'), "-").concat(day.toString().padStart(2, '0'), " ").concat(hour.toString().padStart(2, '0'), ":").concat(minute.toString().padStart(2, '0'), ":").concat(second.toString().padStart(2, '0'));
}
